var gulp = require('gulp');
var gulpLess = require('gulp-less');
var AtomLess = function(e,path){
    e.addListener('taskdefine',function(){
        gulp.task('less',function(){
            gulp.src()
        })
    });
    e.addListener('watch',function(path){
        gulp.watch();
    });
};
AtomLess.name = 'less';
module.exports = AtomLess;
